import React, {useState} from 'react';
import { View, Text, SafeAreaView, StyleSheet, TouchableOpacity, TouchableWithoutFeedback, FlatList, Platform, Dimensions, Image, ScrollView } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';
import { Octicons } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';
import { Button, TextInput, RadioButton, Divider } from 'react-native-paper';
import AsyncStorage from '@react-native-async-storage/async-storage';

import plants from '../components/plants';

 // IMPORT PERSONAL COMPONENTS
//  import BusFooter from  '../components/BusFooter';
//  import Card from '../components/Card'

const {width, height} = Dimensions.get('screen');
import { LinearGradient } from 'expo-linear-gradient';
import * as Animatable from 'react-native-animatable';


const TravellerDetailsScreen = ({navigation}) => {
    const [fullname, setFullname] = useState('');
    const [age, setAge] = useState('');
    const [citizenship, setCitizenship] = useState('');
    const [idNumber, setIdNumber] = useState('');
    const [checked, setChecked] = useState('Male');


    return (
        <SafeAreaView style={styles.mainContainer}>
            <View style={styles.headerContainer}>
                <View style={styles.drawerBarsIcon}>
                <Ionicons name="arrow-back-outline" size={24} color='#003c30' onPress={() => navigation.goBack()} />

                </View>

                <View>
                    <Text style={styles.headerTitle}>TRAVELLER DETAILS</Text>
                </View>
            </View>

            <View style={styles.formDetailsBg}>

                <View style={styles.form__background}>
                    <ScrollView contentContainerStyle={styles.contentContainer}>
                        <View style={styles.form__container}>
                            <View style={{flexDirection: 'row', justifyContent: 'flex-start', alignItems: 'flex-start'}}>
                                <Text style={styles.section__title}>Traveller Information</Text>
                                <Divider />
                            </View>

                            <View style={{flexDirection: 'row', justifyContent: 'flex-start', alignItems: 'flex-start'}}>
                                <Text style={styles.yellow_label}>Note: Name should be same as in ID proof.</Text>
                                <Divider />
                            </View>

                            <View style={styles.firstDetailsText}>
                                <Text>Passenger 2</Text>
                                <Text>Seat No : 12</Text>
                                <Text>Fare : GH₵ 42</Text>
                            </View>

                            <View style={styles.radioButton__container}>
                                <View style={{flexDirection: 'row',justifyContent: 'center', alignItems: 'center', marginRight: 20,}}>
                                    <RadioButton
                                    label="Male"
                                        value="Male"
                                        color="#003c30"
                                        status={ checked === 'Male' ? 'checked' : 'unchecked' }
                                        onPress={() => setChecked('Male')}
                                    />
                                <Text>Male</Text> 
                                </View>

                                <View style={{flexDirection: 'row',justifyContent: 'center', alignItems: 'center'}}>
                                    <RadioButton
                                    label="Female"
                                        value="Female"
                                        color="#003c30"
                                        status={ checked === 'Female' ? 'checked' : 'unchecked' }
                                        onPress={() => setChecked('Female')}
                                    />
                                <Text>Female</Text> 
                                </View>

                            </View>

                            <View style={styles.form__input__container}>
                                <TextInput style={styles.text__input} theme={{ colors: { primary: '#003c30',underlineColor:'transparent'}}} mode="outlined" selectionColor="#003c30" underlineColor="#003c30" outlineColor="grey" label="Full Name" value={fullname} placeholder="Full Name" onChangeText={fullname => setFullname(fullname)} />
                            </View>

                            <View style={styles.form__input__container}>
                                <TextInput style={styles.text__input} theme={{ colors: { primary: '#003c30',underlineColor:'transparent'}}} mode="outlined" selectionColor="#003c30" underlineColor="#003c30" outlineColor="grey" label="Age" value={age} placeholder="Age" onChangeText={age => setAge(age)} />
                            </View>

                            <View style={styles.form__input__container}>
                                <TextInput style={styles.text__input} theme={{ colors: { primary: '#003c30',underlineColor:'transparent'}}} mode="outlined" selectionColor="#003c30" underlineColor="#003c30" outlineColor="grey" label="Citizenship" value={citizenship} placeholder="Citizenship" onChangeText={citizenship => setCitizenship(citizenship)} />
                            </View>

                            <View style={{flexDirection: 'row', justifyContent: 'flex-start', alignItems: 'flex-start'}}>
                                <Text style={styles.section__title}>Contact Information</Text>
                                <Divider />
                            </View>

                            <View style={styles.form__input__container}>
                                <TextInput style={styles.text__input} theme={{ colors: { primary: '#003c30',underlineColor:'transparent'}}} mode="outlined" selectionColor="#003c30" underlineColor="#003c30" outlineColor="grey" label="Mobile Number" value={age} placeholder="Mobile Number" onChangeText={age => setAge(age)} />
                            </View>

                            <View style={styles.form__input__container}>
                                <TextInput style={styles.text__input} theme={{ colors: { primary: '#003c30',underlineColor:'transparent'}}} mode="outlined" selectionColor="#003c30" underlineColor="#003c30" outlineColor="grey" label="Kin Name" value={age} placeholder="Kin Name" onChangeText={age => setAge(age)} />
                            </View>

                            <View style={styles.form__input__container}>
                                <TextInput style={styles.text__input} theme={{ colors: { primary: '#003c30',underlineColor:'transparent'}}} mode="outlined" selectionColor="#003c30" underlineColor="#003c30" outlineColor="grey" label="Kin Mobile Number" value={age} placeholder="Kin Mobile Number" onChangeText={age => setAge(age)} />
                            </View>

                            <TouchableWithoutFeedback
                            style={styles.signIn}
                            onPress={() => {navigation.navigate('Journey')}}>
                                <LinearGradient
                                colors={['#003c30',  '#0f352d']}
                                style={styles.signIn}>
                                    <Text style={[styles.textSign, {
                                        color:'#fff'
                                    }]}>PROCEED TO BOOK</Text>
                                </LinearGradient>
                            </TouchableWithoutFeedback>
                        </View>
                    </ScrollView>
                </View>



            </View>

        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    mainContainer: {
        flex: 1,
        backgroundColor: '#003c30',
    },
    headerContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        // justifyContent: 'space-between',
        marginTop: 35, 
        paddingVertical: 15,
    },
    drawerBarsIcon: {
        width: 50,
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#FFF',
        marginLeft: 25,
        marginRight: 20,
        borderRadius: 20,
        // padding: 15,
        shadowColor: 'black',
