import React, {useState} from 'react';
import { View, Text, SafeAreaView, StyleSheet, TouchableWithoutFeedback, Platform, Dimensions, Image, ScrollView, Button,TextInput } from 'react-native';
import { FontAwesome } from '@expo/vector-icons';
