import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
import {TouchableRipple } from 'react-native-paper';


const {width, height} = Dimensions.get('screen');
import { LinearGradient } from 'expo-linear-gradient';
import * as Animatable from 'react-native-animatable';


const FormDetailsScreen = ({route, navigation}) => {
    const { cityFrom, cityTo, tripDate } = route.params;

   console.log(JSON.parse(tripDate));

    return (
        <SafeAreaView style={styles.mainContainer}>
            <View style={styles.headerContainer}>
                <View style={styles.drawerBarsIcon}>
                <Ionicons name="arrow-back-outline" size={24} color='#003c30' onPress={() => navigation.goBack()} />

                </View>

                <View>
                    <Text style={styles.headerTitle}>STC BUSES</Text>
                </View>
            </View>

            <View style={styles.formDetailsBg}>

                <View style={styles.form__details__section}>
                    <View style={styles.img__bg}>
                        <Image style={styles.destination_from_img} source={require('../images/icons/from.png')} />
                    </View>


                    <View style={styles.detailsText}>
                        <Text style={styles.destination_small_form}>From : <Text style={styles.form__details__padding}>{JSON.parse(JSON.stringify(cityFrom))}</Text></Text>
                        <Text style={styles.destination_small_form}>To : <Text>{JSON.parse(JSON.stringify(cityTo))}</Text></Text>
                        <Text style={styles.destination_small_form}>Date : <Text>{JSON.parse(tripDate)}</Text></Text>
                    </View>
                </View>

                <TouchableWithoutFeedback
                    style={styles.sinIn}
                    onPress={() => {navigation.navigate('BusSeatPicker',{
                        cityFrom: cityFrom,
                        cityTo: cityTo,
                        tripDate: tripDate,
                    })}}>


                    <LinearGradient colors={['#002d24', '#003c30', '#002d24']} style={styles.stc__bus__bg}>

                        <View style={styles.form__navigation__seat__page}>
                            <View>
                                <Text style={styles.destination_small_form_api}>ACC(TUDU) - (MAIN) - KASOA(GA) - CAPE - TADI - TARKWA</Text>
                            </View>

                            <View style={styles.row__column}>
                                <View style={styles.img__bg}>
                                    <Image style={styles.destination_from_img_fare_seat} source={require('../images/bus.png')} />
                                </View>

                                <View style={styles.detailsText_fare}>
                                    <Text style={styles.destination_small_form_grey}>Departure Time - 6 : 00 AM</Text>
                                    <Text style={styles.destination_small_form_grey}>44 Totals seats - 44 Seats left</Text>
                                    <View style={styles.two__block__float}>
                                    <Text style={styles.destination_small_form_fare}>GH₵ 100</Text>
                                    <FontAwesome name="chevron-right" size={14} color="#FFF" />
                                    </View>
                                </View>
                            </View>
                        </View>

                    </LinearGradient>
                </TouchableWithoutFeedback>



            </View>

        </SafeAreaView>
    )
}

