import React, {useState} from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';

import { Snackbar } from 'react-native-paper';

// REDUX
// import {connect} from 'react-redux';
// import * as actions from '../Redux/Actions/cartActions'
import { useStateValue } from '../src/StateProvider';

const Card = (data) => {

    const [visible, setVisible] = useState('Hide');
    // const onToggleSnackBar = () => setVisible(!visible);
    const onDismissSnackBar = () => setVisible(false);

    const { props } = data;

    const [{basket}, dispatch] = useStateValue();
    const addToBasket = () => {
        dispatch({
            type: 'ADD_TO_BASKET',
            item: {
                id: props.id,
            }
        })
    }

    const [isSelected, setIsSelected] = useState(false);


    const handleSelectLogic = () => {
        if(props.booked == false){
            if(!isSelected){
                setIsSelected(true); 
            }
        } 
    }

    const handleUnselectLogic = () => {
        if(props.booked == false){
            if(isSelected){
                setIsSelected(false);
                // alert('I am select')
            }
        } 
    }


    return <>
    {props.booked === true && (
        <TouchableOpacity onPress={() => {alert('I am Booked')}}>
            <View style={styles.card}>
                <View style={styles.centered__seat__number}>
                       <Image style={styles.flatList__seat} source={require('../images/icons/booked.png')} />
                    <Text style={styles.seat__numbering}>{props.id}</Text>
                </View>
            </View>
        </TouchableOpacity>
    )}

    {isSelected == true && (
        <TouchableOpacity onPress={handleUnselectLogic}>
            <View style={styles.card}>
                <View style={styles.centered__seat__number}>
                    <Image style={styles.flatList__seat} source={require('../images/icons/selected.png')} />
                    <Text style={styles.seat__numbering}>{props.id}</Text>
                </View>
            </View>
        </TouchableOpacity>
    )}

    {props.booked == false && !isSelected  && (
        <TouchableOpacity onPress={handleSelectLogic}>
            <View style={styles.card}>
                <View style={styles.centered__seat__number}>
                    <Image style={styles.flatList__seat} source={require('../images/icons/seat.png')} />
                    <Text style={styles.seat__numbering}>{props.id}</Text>
                </View>
            </View>
        </TouchableOpacity>
    )}

        </>

}


const styles = StyleSheet.create({
    card: {
        height: 60,
        backgroundColor: '#eaf2e9',
        width: 60,
        marginHorizontal: 5,
        marginBottom: 10,
        padding: 10,
        borderRadius: 5,
        justifyContent: 'center',
        alignItems: 'center',
        shadowColor: "#a5a5a6",
        shadowOffset: { width: 0, height: 10},
        shadowOpacity: .5,
        shadowRadius: 20,
        elevation: 2,
    },
    centered__seat__number: {
        justifyContent: 'center',
        alignItems: 'center',
    },
    flatList__seat: {
        width: 25,
        height: 25,
    },
    seat__numbering: {
        fontSize: 10,
        fontFamily: 'Oswald-Regular'
    },
})

export default Card;
